import React, { useEffect, useState } from "react";
import CardComponent from "../../components/CardComponent";
import axios from "axios";
import { useSelector } from "react-redux";
import { Container, Grid, Button } from "@mui/material";
import { Link, Navigate } from "react-router-dom";
import ROUTES from "../../routes/ROUTES";

const MyCardsPage = () => {
  const [setDataFromServer] = useState([]);
  const [myCards, setMyCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const userData = useSelector((state) => state.authSlice.userData);

  useEffect(() => {
    const fetchMyCards = async () => {
      try {
        setLoading(true);

        // Add a check for userData
        if (!userData) {
          // You may want to handle this case, for example, redirect to login
          console.error("User data is undefined");
          setLoading(false);
          return;
        }

        const userId = userData._id; // Replace with the user's ID
        const config = {
          headers: {
            "x-auth-token": "YOUR_TOKEN_HERE", // Replace with your token
          },
        };

        const dataFromServer = await axios.get(
          `https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards/my-cards`,
          config
        );

        const userCards = dataFromServer.data.filter(
          (card) => card.user_id === userId
        );
        setMyCards(userCards);
        setLoading(false);
      } catch (error) {
        setError("Error fetching user's cards");
        setLoading(false);
      }
    };

    fetchMyCards();
  }, [userData]);

  const handleEditCard = (_id) => {
    Navigate(`${ROUTES.EDITCARD}/${_id}`);
  };

  const handleDeleteCard = (_id) => {
    console.log("_id to delete (MyCards)", _id);
    setDataFromServer((dataFromServerCopy) =>
      dataFromServerCopy.filter((card) => card._id !== _id)
    );
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <Container
      sx={{ display: "flex", flexDirection: "column", alignItems: "center" }}
    >
      <Link to={ROUTES.CREATECARD}>
        <Button
          variant="outlined"
          sx={{ mt: 3, bgcolor: "green", color: "white" }}
        >
          Create Card
        </Button>
      </Link>

      <Grid container spacing={2} sx={{ marginTop: 2 }}>
        {myCards.length > 0 ? (
          myCards.map((card) => (
            <Grid item key={card._id} xs={12} sm={6} md={4} lg={3}>
              <CardComponent
                _id={card._id}
                title={card.title}
                subTitle={card.subtitle}
                phone={card.phone}
                address={`${card.address.city}, ${card.address.street} ${card.address.houseNumber}`}
                img={card.image && card.image.url}
                alt={card.image && card.image.alt}
                onDelete={handleDeleteCard}
                onEditCard={handleEditCard}
              />
            </Grid>
          ))
        ) : (
          <Grid item>
            <div>No cards found.</div>
          </Grid>
        )}
      </Grid>
    </Container>
  );
};

export default MyCardsPage;
