import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import axios from "axios";

const LoginPage = () => {
  const [emailValue, setEmailValue] = useState("");
  const [passwordValue, setPasswordValue] = useState("");
  const [rememberMe, setRememberMe] = useState(true);
  const [errorsState, setErrorsState] = useState(null);
  const [loginAttempts, setLoginAttempts] = useState(0);
  const [isBlocked, setIsBlocked] = useState(false);

  // Simulated login validation function (replace with your actual validation logic)
  const validateLogin = (email, password) => {
    // Replace this with your validation logic
    if (email === "user@example.com" && password === "password123") {
      return true;
    }
    return false;
  };

  const handleSubmit = async (event) => {
    if (isBlocked) {
      toast("Your account is blocked. Please try again later.");
      return;
    }

    try {
      event.preventDefault();
      const isValid = validateLogin(emailValue, passwordValue);
      if (!isValid) {
        throw new Error("Invalid credentials");
      }

      // Successful login
      resetLoginState();
      toast("You logged in successfully ðŸ‘Œ");
    } catch (err) {
      console.log("Login error:", err);
      setLoginAttempts(loginAttempts + 1);

      if (loginAttempts >= 2) {
        setIsBlocked(true);
        // Simulate storing the block timestamp in localStorage or a server-side database
        localStorage.setItem("blockedUser", Date.now());
      }
    }
  };

  useEffect(() => {
    // Check if the user is unblocked
    const blockedUserTimestamp = localStorage.getItem("blockedUser");
    if (blockedUserTimestamp) {
      const now = Date.now();
      const blockedUserTime = parseInt(blockedUserTimestamp);

      if (now - blockedUserTime >= 24 * 60 * 60 * 1000) {
        // If 24 hours have passed, unblock the user
        setIsBlocked(false);
        setLoginAttempts(0);
        localStorage.removeItem("blockedUser");
      }
    }
  }, [isBlocked]);

  const resetLoginState = () => {
    setEmailValue("");
    setPasswordValue("");
    setRememberMe(true);
    setErrorsState(null);
  };

  const handleEmailInputChange = (e) => {
    setEmailValue(e.target.value);
  };

  const handlePasswordInputChange = (e) => {
    setPasswordValue(e.target.value);
  };

  const handleRememberMeChange = () => {
    setRememberMe(!rememberMe);
  };
  return (
    <Grid container component="main" sx={{ height: "100vh" }}>
      <CssBaseline />
      <Grid
        item
        xs={false}
        sm={4}
        md={7}
        sx={{
          backgroundImage: "url(https://source.unsplash.com/random?wallpapers)",
          backgroundRepeat: "no-repeat",
          backgroundColor: (t) =>
            t.palette.mode === "light"
              ? t.palette.grey[50]
              : t.palette.grey[900],
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <Box
          sx={{
            my: 8,
            mx: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <Box
            component="form"
            noValidate
            onSubmit={handleSubmit}
            sx={{ mt: 1 }}
          >
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              value={emailValue}
              onChange={handleEmailInputChange}
            />
            {errorsState && errorsState.email && (
              <Alert severity="warning">{errorsState.email}</Alert>
            )}
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              value={passwordValue}
              onChange={handlePasswordInputChange}
            />
            {errorsState && errorsState.password && (
              <Alert severity="warning">{errorsState.password}</Alert>
            )}
            <FormControlLabel
              control={
                <Checkbox
                  value="remember"
                  color="primary"
                  checked={rememberMe}
                  onChange={handleRememberMeChange}
                />
              }
              label="Remember me"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2">
                  Forgot password?
                </Link>
              </Grid>
              <Grid item>
                <Link href="#" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
            <CopyrightComponent sx={{ mt: 5 }} />
          </Box>
        </Box>
      </Grid>
    </Grid>
  );
};

export default LoginPage;
