import { useState } from "react";
import {
  Container,
  TextField,
  Grid,
  Typography,
  Divider,
  Button,
} from "@mui/material";
import { Link, useParams } from "react-router-dom";
import ROUTES from "../../routes/ROUTES";
import axios from "axios";

const CreateCardPage = () => {
  const [inputValue, setInputValue] = useState({
    title: "",
    subtitle: "",
    phone: "",
    add: "",
    mail: "",
    description: "",
    web: "",
    url: "",
  });
  const handleCreateCard = (e) => {
    setInputValue((currentState) => ({
      ...currentState,
      [e.target.id]: e.target.value,
    }));
  };
  const handleCreateClick = async () => {
    try {
      const { data } = await axios.post("/cards", {
        title: inputValue.title,
        subtitle: inputValue.subtitle,
        description: inputValue.description,
        phone: inputValue.phone,
        mail: inputValue.mail,
        web: inputValue.web,
        image: {
          url: inputValue.url,
        },
      });
             console.log("data", data);
    } catch (err) {
      console.log("err", err);
    }
  };
  return (
    <Container sx={{ padding: "50px" }}>
      <Typography variant="h2" sx={{ mb: 1, padding: "10px", pb: "0px" }}>
        create card
      </Typography>
      <Typography variant="body1" sx={{ mb: 1, padding: "3px", ml: "7px" }}>
        Put a new values in the correct input
      </Typography>
      <Divider sx={{ mb: 3 }} />
      <Grid container flexDirection={"column"}>
        <TextField
          id="title"
          label="Title"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.title}
          required
        />
        <TextField
          id="subtitle"
          label="SubTitle"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.subtitle}
          required
        />
        <TextField
          id="phone"
          label="Phone Number"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.phone}
          required
        />
        <TextField
          id="description"
          label="Description"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.description}
          required
        />
        <TextField
          id="web"
          label="Web"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.web}
        />
        <TextField
          id="mail"
          label="Email"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.mail}
          required
        />

        <TextField
          id="url"
          label="Url"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.url}
        />
      </Grid>
      <Grid>
        <Grid item lg={8} md={8} sm={8} xs>
          {" "}
          <Link to={ROUTES.HOME}>
            <Button
              variant="outlined"
              sx={{ mt: 2, width: "100%", ml: "0%", bgcolor: "lightskyblue" }}
              onChange={handleCreateCard}
              onClick={handleCreateClick}
            >
              {" "}
              create{" "}
            </Button>
          </Link>
        </Grid>
      </Grid>
    </Container>
  );
};
export default CreateCardPage;
