import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  loggedIn: false,
  userData: undefined,
  isBusiness: false,
  isAdmin: false,
};

const LeftDrawer = createSlice({
  name: "LeftDrawer",
  initialState,
  reducers: {
    logout: (state) => {
      // Handle logout logic here
      state.register = false;
      state.login = false;
      state.About = false;
    },
    isBusiness: (state) => {
      // Handle isBusiness logic here
      state.About = true;
      state.FavoriteCardPage = true;
      state.MyCardsPage = true;
    },
    isAdmin: (state) => {
      // Handle isAdmin logic here
      state.About = true;
      state.FavoriteCardPage = true;
      state.MyCardsPage = true;
      state.Sandbox = true;
    },
  },
});

export const { logout, isBusiness, isAdmin } = LeftDrawer.actions;
export default LeftDrawer.reducer;
