export const handleMenuClose = (item) => {
  // Implement your logic for closing the menu
  if (item === "Logout") {
    localStorage.removeItem("token");
    setIsLoggedIn(false); // You need to define isLoggedIn and setIsLoggedIn
    window.location.reload();
  }
};
