import React, { useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import { handleMenuClose } from "../";

const FooterComponent = ({ userStatus, isBusiness, isAdmin, isLoggedIn }) => {
  const [value, setValue] = useState(0);

  const myLinks = [
    { to: "/about", children: "About" },
    { to: "/Fav Cards", children: "Favorite Cards" },
    { to: "/My Cards", children: "My Cards" },
  ];

  const getIconForLink = (link) => {
    switch (link) {
      case "/about":
        return <InfoIcon />;
      case "/Fav Cards":
        return <FavoriteIcon />;
      case "/My Cards":
        return <PortraitIcon />;
      default:
        return null;
    }
  };

  const actions = myLinks.map((link) => {
    return {
      label: link.children,
      icon: getIconForLink(link.to),
      to: link.to,
    };
  });

  const filterActions = (actionsArray) => {
    if (userStatus === "loggedOut") {
      return actionsArray.filter((action) => action.to === "/about");
    } else if (isBusiness || isAdmin) {
      return actionsArray;
    } else {
      return actionsArray.filter((action) => action.to === "/about");
    }
  };

  const filteredActions = filterActions(actions);

  const handleLogout = () => {
    if (isLoggedIn) {
      // Implement your logout logic here
      // For example, you can clear user data and perform a logout action
      localStorage.removeItem("token"); // Clear the token
      // You may need to clear other user data and perform additional actions
      // Redirect to the login page or another appropriate action
      window.location.reload(); // Reload the page for the sake of this example
    }
  };

  return (
    <div>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {filteredActions.map((action, index) => (
          <BottomNavigationAction
            key={index}
            label={action.label}
            icon={action.icon}
            component={Link}
            to={action.to}
          />
        ))}
      </BottomNavigation>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default FooterComponent;
