  return (
    <Fragment>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {loggedIn ? (
          <BottomNavigationAction
            label="About"
            icon={<InfoIcon />}
            component={Link}
            to={ROUTES.ABOUT}
          />
        ) : null}
        {loggedIn && (
          <BottomNavigationAction
            label="Favorite Cards"
            icon={<FavoriteIcon />}
            component={Link}
            to={ROUTES.FAVCARD}
          />
        )}
        {(isBusiness || isAdmin) && (
          <BottomNavigationAction
            label="My Cards"
            icon={<PortraitIcon />}
            component={Link}
            to={ROUTES.MYCARDS}
          />
        )}
      </BottomNavigation>
    </Fragment>
  );
};

export default FooterComponent;
