import React, { useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";

const FooterComponent = ({ userStatus, isBusiness, isAdmin }) => {
  const [value, setValue] = useState(0);

  const actions = [
    { label: "About", icon: <InfoIcon />, to: "/about" },
    { label: "Favorite Cards", icon: <FavoriteIcon />, to: "/Fav Cards" },
    { label: "My Cards", icon: <PortraitIcon />, to: "/My Cards" },
  ];

  const filterActions = (actionsArray) => {
    if (userStatus === "loggedOut") {
      return actionsArray.filter((action) => action.to === "/about");
    } else if (isBusiness || isAdmin) {
      return actionsArray;
    } else {
      return actionsArray.filter((action) => action.to === "/about");
    }
  };

  const filteredActions = filterActions(actions);

  return (
    <div>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {filteredActions.map((action, index) => (
          <BottomNavigationAction
            key={index}
            label={action.label}
            icon={action.icon}
            component={Link}
            to={action.to}
          />
        ))}
      </BottomNavigation>
    </div>
  );
};

export default FooterComponent;
