import React, { useEffect, useState } from "react";
import CardComponent from "../../components/CardComponent";
import axios from "axios";
import { useSelector } from "react-redux";
import { Container, Grid } from "@mui/material";
import { Navigate } from "react-router-dom";

const FavoriteCardPage = () => {
  const [dataFromServer, setDataFromServer] = useState([]);
  const [likedCards, setLikedCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState("");
  const [initialDataFromServer, setInitialDataFromServer] = useState([]);

  const userData = useSelector((bigPie) => bigPie.authSlice.userData);

  const handleDeleteCard = (_id) => {
    console.log("_id to delete (HomePage)", _id);

    const updatedData = dataFromServer.filter((card) => card._id !== _id);

    setDataFromServer(updatedData);
    setLikedCards(likedCards.filter((card) => card._id !== _id));
  };

  useEffect(() => {
    const fetchLikedCards = async () => {
      try {
        setLoading(true);
        const userId = userData?._id;
        const dataFromServer = await axios.get(
          "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards"
        );
        const cards = dataFromServer.data.filter((card) =>
          card.likes.includes(userId)
        );

        setLikedCards(cards);
        setInitialDataFromServer(dataFromServer.data);
        setLoading(false);
      } catch (error) {
        setError("Error fetching liked card IDs");
        setLoading(false);
      }
    };

    fetchLikedCards();
  }, [userData?._id]);
  useEffect(() => {
    if (!initialDataFromServer.length) return;
    const filter = query ? query : "";
    setDataFromServer(
      initialDataFromServer.filter((card) => card.title.startsWith(filter))
    );
  }, [query, initialDataFromServer]);

  const handleEditCard = (_id) => {
    Navigate(`${ROUTES.EDIT_CARD}/${_id}`);
  };

  const handleLikeRemove = (_id) => {
    const updatedLikedCards = likedCards.filter((card) => card._id !== _id);
    setLikedCards(updatedLikedCards);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <Container>
      <Grid container spacing={2}>
        {likedCards.length > 0 ? (
          likedCards.map((card) => (
            <Grid item key={card._id} xs={12} sm={6} md={4} lg={3}>
              <CardComponent
                _id={card._id}
                title={card.title}
                subTitle={card.subtitle}
                phone={card.phone}
                address={`${card.address.city}, ${card.address.street} ${card.address.houseNumber}`}
                img={card.image && card.image.url}
                alt={card.image && card.image.alt}
                like={true}
                onToggleFavorite={() => handleLikeRemove(card._id)}
                onDeleteCard={() => handleDeleteCard(card._id)}
                onEditCard={() => handleEditCard(card._id)}
              />
            </Grid>
          ))
        ) : (
          <div>No liked cards found.</div>
        )}
      </Grid>
    </Container>
  );
};

export default FavoriteCardPage;
