import React, { Fragment, useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import ROUTES from "../../routes/ROUTES.js";
import { authActions } from "../";

const FooterComponent = ({ userStatus, isBusiness, isAdmin }) => {
  const [value, setValue] = useState(0);

  // Common actions available to all users.
  const commonActions = [
    <BottomNavigationAction
      label="About"
      icon={<InfoIcon />}
      component={Link}
      to={ROUTES.ABOUT}
    />,
  ];

  // Actions available when the user is logged in.
  const loggedInActions = [
    ...commonActions,
    <BottomNavigationAction
      label="Favorite Cards"
      icon={<FavoriteIcon />}
      component={Link}
      to={ROUTES.FAVCARD}
    />,
  ];

  // Additional actions for Business or Admin users.
  if (isBusiness || isAdmin) {
    loggedInActions.push(
      <BottomNavigationAction
        label="My Cards"
        icon={<PortraitIcon />}
        component={Link}
        to={ROUTES.MYCARDS}
      />
    );
  }

  return (
    <Fragment>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {userStatus === "loggedOut" ? commonActions : loggedInActions}
      </BottomNavigation>
    </Fragment>
  );
};

export default FooterComponent;
