import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  darkTheme: false,
};

const darkThemeSlice = createSlice({
  name: "darkTheme",
  initialState,
  reducers: {
    changeTheme(state) {
      state.darkTheme = !state.darkTheme;
    },
  },
});

export const darkThemeActions = darkThemeSlice.actions;

export default darkThemeSlice.reducer;
