// LoginPage.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useDispatch } from "react-redux";
import { useAutoLogin } from "./useAutoLogin"; // Import useAutoLogin and other dependencies as needed
import { storeToken } from "./storeToken"; // Import storeToken and other dependencies as needed
import { validateLogin } from "./validateLogin"; // Import validateLogin and other dependencies as needed
import ROUTES from "../../routes/ROUTES.js";
import YourLoginFormComponent from "./YourLoginFormComponent"; // Import your login form component


import FooterComponent from "./FooterComponent"; // Import your FooterComponent

const LoginPage = () => {
  const [emailValue, setEmailValue] = useState("");
  const [passwordValue, setPasswordValue] = useState("");
  const [rememberMe, setRememberMe] = useState(true);
  const [errorsState, setErrorsState] = useState(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const autoLogin = useAutoLogin();

  const handleSubmit = async (event) => {
    try {
      event.preventDefault();
      const joiResponse = validateLogin({
        email: emailValue,
        password: passwordValue,
      });
      console.log("joiResponse", joiResponse);
      setErrorsState(joiResponse);
      if (joiResponse) return;
      let { data } = await axios.post("/users/login", {
        email: emailValue,
        password: passwordValue,
      });
      storeToken(data, rememberMe);
      console.log("data from login", data);
      toast("You logged in successfully 👌", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
      });
      autoLogin(true);
      navigate(ROUTES.HOME);
    } catch (err) {
      console.log("err from login", err);
    }
  };

  const handleEmailInputChange = (e) => {
    setEmailValue(e.target.value);
  };

  const handlePasswordInputChange = (e) => {
    setPasswordValue(e.target.value);
  };

  const handleRememberMeChange = () => {
    setRememberMe(!rememberMe);
  };

  return (
    <div>
      <YourLoginFormComponent
        emailValue={emailValue}
        passwordValue={passwordValue}
        rememberMe={rememberMe}
        errorsState={errorsState}
        handleEmailInputChange={handleEmailInputChange}
        handlePasswordInputChange={handlePasswordInputChange}
        handleRememberMeChange={handleRememberMeChange}
        handleSubmit={handleSubmit}
      />
      <FooterComponent userStatus="loggedOut" />
    </div>
  );
};

export default LoginPage;
