import React, { useEffect, useState } from "react";
import CardComponent from "../../components/CardComponent";
import axios from "axios";
import { useSelector } from "react-redux";
import { Container, Grid, Typography, CircularProgress } from "@mui/material";
import { styled } from "@mui/system";

const StyledLoading = styled("div")({
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  height: "200px", // Adjust height based on your preference
});

const StyledError = styled("div")({
  color: "red",
  textAlign: "center",
  marginTop: "20px",
});

const FavoriteCardPage = () => {
  const [likedCards, setLikedCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const userData = useSelector((bigPie) => bigPie.authSlice.userData);

  useEffect(() => {
    const fetchLikedCards = async () => {
      try {
        setLoading(true);
        const userId = userData._id;
        const dataFromServer = await axios.get(
          "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards"
        );
        const cards = dataFromServer.data.filter((card) =>
          card.likes.includes(userId)
        );

        setLikedCards(cards);
        setLoading(false);
      } catch (error) {
        setError("Error fetching liked card IDs");
        setLoading(false);
      }
    };

    fetchLikedCards();
  }, [userData]);

  const handleDeleteCard = (_id) => {
    axios
      .delete(
        `https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards/${_id}`
      )
      .then((response) => {
        setLikedCards((likedCardsCopy) =>
          likedCardsCopy.filter((card) => card._id !== _id)
        );
      })
      .catch((error) => {
        console.error("Error deleting card:", error);
      });
  };

  const handleLikeRemove = (_id) => {
    const updatedLikedCards = likedCards.filter((card) => card._id !== _id);
    setLikedCards(updatedLikedCards);
  };

  if (loading) {
    return (
      <StyledLoading>
        <CircularProgress />
      </StyledLoading>
    );
  }

  if (error) {
    return <StyledError>Error: {error}</StyledError>;
  }

  return (
    <Container>
      <Typography
        variant="h3"
        sx={{ display: "flex", justifyContent: "center", marginTop: 2 }}
      >
        Favorite Cards
      </Typography>
      <Grid container spacing={2} sx={{ marginTop: 2 }}>
        {likedCards.length > 0 ? (
          likedCards.map((card) => (
            <Grid item key={card._id} xs={12} sm={6} md={4} lg={3}>
              <CardComponent
                _id={card._id}
                title={card.title}
                subTitle={card.subtitle}
                phone={card.phone}
                address={`${card.address.city}, ${card.address.street} ${card.address.houseNumber}`}
                img={card.image && card.image.url}
                alt={card.image && card.image.alt}
                like={true} 
                onToggleFavorite={() => handleLikeRemove(card._id)}
                onDeleteCard={() => handleDeleteCard(card._id)}
              />
            </Grid>
          ))
        ) : (
          <Typography
            variant="body1"
            sx={{ textAlign: "center", marginTop: 2 }}
          >
            No liked cards found.
          </Typography>
        )}
      </Grid>
    </Container>
  );
};

export default FavoriteCardPage;
