import React, { useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import UserLinks from "../../routes/ROUTES";

const FooterComponent = ({ userStatus, isBusiness, isAdmin }) => {
  const [value, setValue] = useState(0);

  const links = UserLinks({ userStatus, isBusiness, isAdmin });

  return (
    <div>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {links.map((link, index) => (
          <BottomNavigationAction
            key={index}
            label={link.children}
            icon={
              link.to === ROUTES.ABOUT ? (
                <InfoIcon />
              ) : link.to === ROUTES.FAVCARD ? (
                <FavoriteIcon />
              ) : link.to === ROUTES.MYCARDS ? (
                <PortraitIcon />
              ) : null
            }
            component={Link}
            to={link.to}
          />
        ))}
      </BottomNavigation>
    </div>
  );
};

export default FooterComponent;
