import React, { Fragment, useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import ROUTES from "../routes/ROUTES";
import { useSelector } from "react-redux";

const FooterComponent = () => {
  const [value, setValue] = useState(0);

  // Use useSelector to access user data and roles from the Redux store
  const userData = useSelector((state) => state.auth.userData);
  const isBusiness = useSelector((state) => state.auth.isBusiness);
  const isAdmin = useSelector((state) => state.auth.isAdmin);

  // Define common actions available to all users
  const commonActions = [
    <BottomNavigationAction
      label="About"
      icon={<InfoIcon />}
      component={Link}
      to={ROUTES.ABOUT}
    />,
  ];

  // Define actions for logged-in users
  const loggedInActions = [
    ...commonActions,
    <BottomNavigationAction
      label="Favorite Cards"
      icon={<FavoriteIcon />}
      component={Link}
      to={ROUTES.FAVCARD}
    />,
  ];

  // Conditionally add "My Cards" action for Business or Admin users
  if (isBusiness || isAdmin) {
    loggedInActions.push(
      <BottomNavigationAction
        label="My Cards"
        icon={<PortraitIcon />}
        component={Link}
        to={ROUTES.MYCARDS}
      />
    );
  }

  return (
    <Fragment>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {userData ? loggedInActions : commonActions}
      </BottomNavigation>
    </Fragment>
  );
};

export default FooterComponent;
