import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import axios from "axios";

const LoginPage = () => {
  const [emailValue, setEmailValue] = useState("");
  const [passwordValue, setPasswordValue] = useState("");
  const [rememberMe, setRememberMe] = useState(true);
  const [errorsState, setErrorsState] = useState(null);
  const [loginAttempts, setLoginAttempts] = useState(0);
  const [isBlocked, setIsBlocked] = useState(false);

  // Simulated login validation function (replace with your actual validation logic)
  const validateLogin = (email, password) => {
    // Replace this with your validation logic
    if (email === "user@example.com" && password === "password123") {
      return true;
    }
    return false;
  };

  const handleSubmit = async (event) => {
    if (isBlocked) {
      toast("Your account is blocked. Please try again later.");
      return;
    }

    try {
      event.preventDefault();
      const isValid = validateLogin(emailValue, passwordValue);
      if (!isValid) {
        throw new Error("Invalid credentials");
      }

      // Successful login
      resetLoginState();
      toast("You logged in successfully ðŸ‘Œ");
    } catch (err) {
      console.log("Login error:", err);
      setLoginAttempts(loginAttempts + 1);

      if (loginAttempts >= 2) {
        setIsBlocked(true);
        // Simulate storing the block timestamp in localStorage or a server-side database
        localStorage.setItem("blockedUser", Date.now());
      }
    }
  };

  useEffect(() => {
    // Check if the user is unblocked
    const blockedUserTimestamp = localStorage.getItem("blockedUser");
    if (blockedUserTimestamp) {
      const now = Date.now();
      const blockedUserTime = parseInt(blockedUserTimestamp);

      if (now - blockedUserTime >= 24 * 60 * 60 * 1000) {
        // If 24 hours have passed, unblock the user
        setIsBlocked(false);
        setLoginAttempts(0);
        localStorage.removeItem("blockedUser");
      }
    }
  }, [isBlocked]);

  const resetLoginState = () => {
    setEmailValue("");
    setPasswordValue("");
    setRememberMe(true);
    setErrorsState(null);
  };

  const handleEmailInputChange = (e) => {
    setEmailValue(e.target.value);
  };

  const handlePasswordInputChange = (e) => {
    setPasswordValue(e.target.value);
  };

  const handleRememberMeChange = () => {
    setRememberMe(!rememberMe);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Email:
          <input
            type="email"
            value={emailValue}
            onChange={handleEmailInputChange}
          />
        </label>
        <br />
        <label>Password:
          <input
            type="password"
            value={passwordValue}
            onChange={handlePasswordInputChange}
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
