import React from "react";
import { Link } from "react-router-dom";
import ROUTES from "../routes/ROUTES";

const UserLinks = ({ userStatus, isBusiness, isAdmin }) => {
  if (isAdmin) {
    return [
      { to: ROUTES.ABOUT, children: "About" },
      { to: ROUTES.FAVCARD, children: "Favorite Cards" },
      { to: ROUTES.MYCARDS, children: "My Cards" },
    ];
  } else if (isBusiness) {
    return [
      { to: ROUTES.ABOUT, children: "About" },
      { to: ROUTES.FAVCARD, children: "Favorite Cards" },
      { to: ROUTES.MYCARDS, children: "My Cards" },
    ];
  } else if (userStatus === "loggedOut") {
    return [{ to: ROUTES.ABOUT, children: "About" }];
  } else {
    // Default for logged-in non-business users
    return [
      { to: ROUTES.ABOUT, children: "About" },
      { to: ROUTES.FAVCARD, children: "Favorite Cards" },
      { to: ROUTES.MYCARDS, children: "My Cards" },
    ];
  }
};

export default UserLinks;
