import React, { useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux"; // Import useSelector from React-Redux.
import { authActions } from "../../store/authSlice"; // Import your auth actions from the Redux slice.

const FooterComponent = ({ userStatus, isBusiness, isAdmin }) => {
  const [value, setValue] = useState(0);

  // Get userData from the Redux store using useSelector.
  const userData = useSelector((state) => state.auth.userData);

  const myLinks = [
    { to: "/about", children: "About" },
    { to: "/Fav Cards", children: "Favorite Cards" },
    { to: "/My Cards", children: "My Cards" },
  ];

  const getIconForLink = (link) => {
    switch (link) {
      case "/about":
        return <InfoIcon />;
      case "/Fav Cards":
        return <FavoriteIcon />;
      case "/My Cards":
        return <PortraitIcon />;
      default:
        return null;
    }
  };

  const actions = myLinks.map((link) => {
    return {
      label: link.children,
      icon: getIconForLink(link.to),
      to: link.to,
    };
  });

  const filterActions = (actionsArray) => {
    if (userStatus === "loggedOut") {
      return actionsArray.filter((action) => action.to === "/about");
    } else if (userData) {
      return actionsArray.filter(
        (action) => action.to === "/about" || action.to === "/Fav Cards"
      ); // Use || operator to filter both links.
    } else if (isBusiness || isAdmin) {
      return actionsArray;
    } else {
      return actionsArray.filter((action) => action.to === "/about");
    }
  };

  const filteredActions = filterActions(actions);

  return (
    <div>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {filteredActions.map((action, index) => (
          <BottomNavigationAction
            key={index}
            label={action.label}
            icon={action.icon}
            component={Link}
            to={action.to}
          />
        ))}
      </BottomNavigation>
    </div>
  );
};

export default FooterComponent;
