import React, { useEffect, useState } from "react";
import { Container, Grid, Pagination, Paper } from "@mui/material";
import CardComponent from "../../components/CardComponent";
import { useNavigate } from "react-router-dom";
import ROUTES from "../../routes/ROUTES";
import axios from "axios";
import homePageNormalization from "./homePageNormalization";
import { useSelector } from "react-redux";
import useQueryParams from "../../hooks/useQueryParams";

const itemsPerPage = 20;

const HomePage = () => {
  const [dataFromServer, setDataFromServer] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const navigate = useNavigate();
  const userData = useSelector((bigPie) => bigPie.authSlice.userData);
  const query = useQueryParams();

  useEffect(() => {
    axios
      .get("/cards")
      .then(({ data }) => {
        if (userData) data = homePageNormalization(data, userData._id);
        console.log("data", data);
        setDataFromServer(data);
      })
      .catch((err) => {
        console.log("err", err);
      });
  }, [userData]);

  useEffect(() => {
    if (!dataFromServer.length) return;
    const filter = query.filter ? query.filter : "";
    console.log("filter", filter);
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const filteredData = dataFromServer
      .filter((card) => card.title.startsWith(filter))
      .slice(startIndex, endIndex);
    setDataFromServer(filteredData);
  }, [query, dataFromServer, currentPage]);

  const handlePageChange = (event, value) => {
    setCurrentPage(value);
  };

  const handleDeleteCard = (_id) => {
    console.log("_id to delete (HomePage)", _id);
    setDataFromServer((dataFromServerCopy) =>
      dataFromServerCopy.filter((card) => card._id !== _id)
    );
  };

  const handleEditCard = (_id) => {
    navigate(`${ROUTES.EDITCARD}/${_id}`);
  };

  const handleLikeCardClick = (_id, isLiked) => {
    setDataFromServer((dataFromServerCopy) =>
      dataFromServerCopy.map((card) => {
        if (card._id === _id) {
          card.like = isLiked;
          if (isLiked) {
            const likedCards =
              JSON.parse(localStorage.getItem("likedCards")) || [];
            likedCards.push(card);
            localStorage.setItem("likedCards", JSON.stringify(likedCards));
          }
        }
        return card;
      })
    );
  };

  return (
    <Container>
      <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px" }}>
        <Grid container spacing={2}>
          {dataFromServer.map((card) => (
            <Grid item key={card._id} xs={12} sm={6} md={4} lg={3}>
              <CardComponent
                _id={card._id}
                title={card.title}
                subTitle={card.subtitle}
                phone={card.phone}
                address={`${card.address.city}, ${card.address.street} ${card.address.houseNumber}`}
                img={card.image.url}
                alt={card.image.alt}
                like={card.likes}
                cardNumber={card.cardNumber}
                onDeleteCard={handleDeleteCard}
                onEditCard={handleEditCard}
                handleLikeCardClick={handleLikeCardClick}
              />
            </Grid>
          ))}
        </Grid>
      </Paper>
      <Pagination
        count={Math.ceil(dataFromServer.length / itemsPerPage)}
        page={currentPage}
        onChange={handlePageChange}
        color="primary"
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-end",
          marginTop: "20px",
          marginBottom: "20px",
        }}
      />
    </Container>
  );
};

export default HomePage;
