import React, { Fragment, useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import ROUTES from "../../routes/ROUTES.js";
import { useSelector, useDispatch } from "react-redux";
import { logOutAction } from "./your-auth-actions"; // Import your logout action

const FooterComponent = () => {
  const [value, setValue] = useState(0);
  const loggedIn = useSelector((bigPie) => bigPie.authSlice.loggedIn);
  const isBusiness = useSelector((bigPie) => bigPie.authSlice.isBusiness);
  const isAdmin = useSelector((bigPie) => bigPie.authSlice.isAdmin);
  const dispatch = useDispatch();

  const handleLogOut = () => {
    // Dispatch your logout action to update the authentication state
    dispatch(logOutAction());
  };

  return (
    <Fragment>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {loggedIn && (
          <BottomNavigationAction
            label="About"
            icon={<InfoIcon />}
            component={Link}
            to={ROUTES.ABOUT}
          />
        )}
        {loggedIn && (
          <BottomNavigationAction
            label="Favorite Cards"
            icon={<FavoriteIcon />}
            component={Link}
            to={ROUTES.FAVCARD}
          />
        )}
        {(isBusiness || isAdmin) && (
          <BottomNavigationAction
            label="My Cards"
            icon={<PortraitIcon />}
            component={Link}
            to={ROUTES.MYCARDS}
          />
        )}
        {loggedIn && ( // Conditionally render a "Log Out" button if the user is logged in
          <BottomNavigationAction
            label="Log Out"
            onClick={handleLogOut}
          />
        )}
      </BottomNavigation>
    </Fragment>
  );
};

export default FooterComponent;
