import { useState } from "react";
import {
  Container,
  TextField,
  Grid,
  Typography,
  Divider,
  Button,
} from "@mui/material";
import { Link } from "react-router-dom"; // Removed unused import
import axios from "axios";
import ROUTES from "../../routes/ROUTES";

const CreateCard = () => {
  const [inputValue, setInputValue] = useState({
    title: "",
    subtitle: "",
    phone: "",
    description: "",
    web: "",
    mail: "",
    url: "",
  });

  const handleCreateCard = (e) => {
    const { id, value } = e.target;
    setInputValue((currentState) => ({
      ...currentState,
      [id]: value,
    }));
  };

  const handleCreateClick = async () => {
    try {
      const { data } = await axios.post("/cards", {
        title: inputValue.title,
        subtitle: inputValue.subtitle,
        description: inputValue.description,
        phone: inputValue.phone,
        mail: inputValue.mail,
        web: inputValue.web,
        image: {
          url: inputValue.url,
        },
      });
      // You can handle the response data here if needed
      console.log("Card created:", data);
    } catch (err) {
      console.error("Error creating card:", err);
    }
  };

  return (
    <Container sx={{ padding: "50px" }}>
      <Typography variant="h2" sx={{ mb: 1, padding: "10px", pb: "0px" }}>
        Create Card
      </Typography>
      <Typography variant="body1" sx={{ mb: 1, padding: "3px", ml: "7px" }}>
        Put new values in the correct input
      </Typography>
      <Divider sx={{ mb: 3 }} />
      <Grid container flexDirection={"column"}>
        <TextField
          id="title"
          label="Title"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.title}
          required
        />
        <TextField
          id="subtitle"
          label="SubTitle"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.subtitle}
          required
        />
        <TextField
          id="phone"
          label="Phone Number"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.phone}
          required
        />
        <TextField
          id="description"
          label="Description"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.description}
          required
        />
        <TextField
          id="web"
          label="Web"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.web}
        />
        <TextField
          id="mail"
          label="Email"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.mail}
          required
        />

        <TextField
          id="url"
          label="Url"
          variant="outlined"
          sx={{ mt: "10px" }}
          onChange={handleCreateCard}
          value={inputValue.url}
        />
      </Grid>
      <Grid>
        <Grid item lg={8} md={8} sm={8} xs={12}>
          <Button
            variant="outlined"
            sx={{ mt: 2, width: "100%", ml: "0%", bgcolor: "lightskyblue" }}
            onClick={handleCreateClick}
          >
            Create
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default CreateCard;
