import React, { Fragment, useState } from "react";
import {
  BottomNavigation,
  BottomNavigationAction,
  Divider,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import ROUTES from "../../routes/ROUTES.js";

const FooterComponent = ({ userStatus }) => {
  const [value, setValue] = useState(0);

  let navigationActions = [];

  if (userStatus === "loggedOut") {
    navigationActions = [
      <BottomNavigationAction
        label="About"
        icon={<InfoIcon />}
        component={Link}
        to={ROUTES.ABOUT}
      />,
    ];
  } else if (userStatus === "loggedIn") {
    navigationActions = [
      <BottomNavigationAction
        label="About"
        icon={<InfoIcon />}
        component={Link}
        to={ROUTES.ABOUT}
      />,
      <BottomNavigationAction
        label="Favorite Cards"
        icon={<FavoriteIcon />}
        component={Link}
        to={ROUTES.FAVCARD}
      />,
    ];
  } else if (userStatus === "business") {
    navigationActions = [
      <BottomNavigationAction
        label="About"
        icon={<InfoIcon />}
        component={Link}
        to={ROUTES.ABOUT}
      />,
      <BottomNavigationAction
        label="Favorite Cards"
        icon={<FavoriteIcon />}
        component={Link}
        to={ROUTES.FAVCARD}
      />,
      <BottomNavigationAction
        label="My Cards"
        icon={<PortraitIcon />}
        component={Link}
        to={ROUTES.MYCARDS}
      />,
    ];
  }

  return (
    <Fragment>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {navigationActions}
      </BottomNavigation>
    </Fragment>
  );
};

export default FooterComponent;
