import React from "react";
import {
  Box,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  Divider,
  Drawer,
  ListItemIcon,
} from "@mui/material";
import HomeIcon from "@mui/icons-material/Home";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import AccountBoxIcon from "@mui/icons-material/AccountBox";
import DeveloperModeIcon from "@mui/icons-material/DeveloperMode";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

  const LeftDrawerComponent = ({ isOpen, onCloseDrawer }) => {
  const loggedIn = useSelector((bigPie) => bigPie.authSlice.loggedIn);
  const isBusiness = useSelector((bigPie) => bigPie.authSlice.isBusiness);
  const isAdmin = useSelector((bigPie) => bigPie.authSlice.isAdmin);

  const listItems = [
    { text: "Home", link: "/", icon: <HomeIcon /> },
    { text: "About", link: "/about", icon: <InfoIcon /> },
    { text: "Favorite Cards", link: "/favoriteCardPage", icon: <FavoriteIcon /> },
  ];

  if (isBusiness) {
    listItems.push(
      { text: "Favorite Card", link: "/favoriteCardPage", icon: <FavoriteIcon /> },
      { text: "My Cards", link: "/mMyCardsPage", icon: <AccountBoxIcon /> }
    );
  }

  if (isAdmin) {
    listItems.push(
      { text: "My Cards", link: "/MyCardsPage", icon: <AccountBoxIcon /> },
      { text: "Sandbox", link: "/sandboxPage", icon: <DeveloperModeIcon /> }
    );
  }

  const renderListItems = () => (
    <List>
      {listItems.map((item) => (
        <ListItem key={item.text} disablePadding>
          <ListItemButton component={Link} to={item.link}>
            <ListItemIcon>{item.icon}</ListItemIcon>
            <ListItemText primary={item.text} />
          </ListItemButton>
        </ListItem>
      ))}
    </List>
  );



  return (
    <Drawer anchor="left" open={isOpen} onClose={onCloseDrawer}>
      {list()}
    </Drawer>
  );
};

export default LeftDrawerComponent;
