import React, { useState } from "react";
import { BottomNavigation, BottomNavigationAction, Divider } from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import FavoriteIcon from "@mui/icons-material/Favorite";
import PortraitIcon from "@mui/icons-material/Portrait";
import { Link } from "react-router-dom";
import UserLinks from "../../routes/ROUTES";


const FooterComponent = ({ userStatus, isBusiness, isAdmin }) => {
  const [value, setValue] = useState(0);

  const commonActions = [
    <BottomNavigationAction
      label="About"
      icon={<InfoIcon />}
      component={Link}
      to={ROUTES.ABOUT}
    />,
  ];

  const loggedInActions = [
    ...commonActions,
    <BottomNavigationAction
      label="Favorite Cards"
      icon={<FavoriteIcon />}
      component={Link}
      to={ROUTES.FAVCARD}
    />,
    <BottomNavigationAction
      label="My Cards"
      icon={<PortraitIcon />}
      component={Link}
      to={ROUTES.MYCARDS}
    />,
  ];

  return (
    <Fragment>
      <Divider />
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        {userStatus === "loggedOut" ? commonActions : loggedInActions}
      </BottomNavigation>
    </Fragment>
  );
};

export default FooterComponent;
